Scenario.Script:
//

	var patches = 25;
	var x0 = 20, z0 = 20, x1 = patches*16*4-20;
	var x = x0, z = z0;
	var maxh = 0;
	var gap = 14;

	var iii = 0;
	while (iii < 5000)
	{
		//var ent = Engine.AddEntity("gaia/flora_tree_cretan_date_palm_patch");	// stupid amount of polys
		//var ent = Engine.AddEntity("gaia/flora_tree_palm_tropical");		// /2 draw calls, x1.2 more polys than oak
		//var ent = Engine.AddEntity("gaia/flora_tree_pine");		// /2 draw calls, x1.2 more polys than oak
		var ent = Engine.AddEntity("gaia/flora_tree_pine_novar");
		//var ent = Engine.AddEntity("gaia/flora_tree_oak");		// low amount of polys
		//var ent = Engine.AddEntity("gaia/flora_tree_poplar_lombardy");	// even lower

		if (!ent) {
			print("Failed to load "+template+"\n");
			continue;
		}

		var cmpFootprint = Engine.QueryInterface(ent, IID_Footprint);
		if (!cmpFootprint) {
			print(template+" has no footprint\n");
			continue;
		}
		var shape = cmpFootprint.GetShape();
		var w, h;
		if (shape.type == 'circle') {
			w = h = shape.radius*2;
		} else {
			w = shape.width;
			h = shape.depth;
		}

		if (x + w >= x1) {
			// Start a new row
			x = x0;
			z += maxh + gap;
			maxh = 0;
		}

		var cmpPosition = Engine.QueryInterface(ent, IID_Position);
		cmpPosition.MoveTo(x + w/2, z);
		cmpPosition.SetYRotation(Math.PI*3/4);

		var cmpOwnership = Engine.QueryInterface(ent, IID_Ownership);
		if (cmpOwnership)
			cmpOwnership.SetOwner(1);

		x += w + gap;
		maxh = Math.max(maxh, h);
		iii++;

	}

	//
